//This Function display icon which indicates to go back to previous page


import React, { useState } from "react";

const Backward = () => {
  const [isClicked, setIsClicked] = useState(false);

  const handleMouseEnter = () => {
    setIsClicked(false);
    document.getElementById("backward-icon").style.border = "2px solid lightgreen";
  };

  const handleMouseLeave = () => {
    document.getElementById("backward-icon").style.border = "none";
  };

  const handleClick = () => {
    setIsClicked(true);
    document.getElementById("backward-icon").style.border = "2px solid green";
  };

  return (
    <svg
      id="backward-icon"
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 24 24"
      fill="currentColor"
      className="w-4 h-4"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onClick={handleClick}
      style={{ borderRadius: "100%" }} 
    >
      <path d="M9.195 18.44c1.25.713 2.805-.19 2.805-1.629v-2.34l6.945 3.968c1.25.714 2.805-.188 2.805-1.628V8.688c0-1.44-1.555-2.342-2.805-1.628L12 11.03v-2.34c0-1.44-1.555-2.343-2.805-1.629l-7.108 4.062c-1.26.72-1.26 2.536 0 3.256l7.108 4.061z" />
    </svg>
  );
};

export default Backward;
